mapper.select:
SELECT c.*, t.`teacher_name`, p.phase_name FROM class c LEFT JOIN teacher t ON c.`class_headmaster` = t.`teacher_id` LEFT JOIN phase p ON c.class_phase = p.phase_id
and c.`class_id` = #{classId}
AND c.`class_name` LIKE '%${className}%'
AND c.`class_graduation` = #{classGraduation}
AND c.`class_phase` = #{classPhase}

SELECT * FROM ( SELECT co.*, t.`teacher_Id`, t.`teacher_name`, ct.`course_type_name` FROM `class_course` cc LEFT JOIN teacher_course tc ON cc.`class_course_id` = tc.`class_course_id` LEFT JOIN teacher t ON tc.`teacher_id` = t.`teacher_id` LEFT JOIN course co ON cc.`course_id` = co.`course_id` LEFT JOIN course_type ct ON co.`course_type` = ct.`course_type_id` WHERE cc.`class_id`=#{classId} AND tc.`state` = 1 union SELECT co.*, t.`teacher_Id`, t.`teacher_name`, ct.`course_type_name` FROM `class_course` cc LEFT JOIN teacher_course tc ON cc.`class_course_id` = tc.`class_course_id` LEFT JOIN teacher t ON tc.`teacher_id` = t.`teacher_id` LEFT JOIN course co ON cc.`course_id` = co.`course_id` LEFT JOIN course_type ct ON co.`course_type` = ct.`course_type_id` WHERE cc.`class_id`=#{classId} AND tc.state is null ) c
AND c.`course_id` = #{courseId}
AND c.`course_name` LIKE '%${courseName}%'
AND c.`course_type` = #{courseType}
AND c.`teacher_id` = #{courseTeacher}

SELECT *
FROM (
    SELECT
    s.*
    FROM student s
    LEFT JOIN class c
    ON s.`student_class` = c.`class_id`
    WHERE c.`class_id`=#{classId}
) s


        AND s.`student_id`=#{studentId}


        AND s.`student_name` like '%${studentName}%'


        AND s.`student_gender`=#{studentGender}


        AND s.`student_state`=#{studentState}



        AND s.`student_phase`=#{studentPhase}


        AND s.`student_graduation`=#{studentGraduation}


        AND s.`student_arrears`=#{studentArrears}

SELECT *
FROM (
    SELECT t.*
    FROM class c
    LEFT JOIN teacher t
    ON c.`class_headmaster` = t.`teacher_id`
    WHERE c.`class_id`= #{classId}
    UNION
    SELECT t.*
    FROM `class_course` cc
    LEFT JOIN teacher_course tc
    ON cc.`class_course_id` = tc.`class_course_id`
    LEFT JOIN teacher t
    ON tc.`teacher_id` = t.`teacher_id`
    WHERE cc.`class_id`=#{classId}
    AND tc.`state` = 1
) t


        AND t.`teacher_id` = #{teacherId}


        AND t.`teacher_name` LIKE '%${teacherName}%'


        AND t.`teacher_gender` = #{teacherGender}


        AND t.`teacher_state` = #{teacherState}


        AND t.`teacher_type` = #{teacherType}
